mapper.select:
SELECT COUNT(*)
FROM USER_INFO
WHERE U_EMAIL = #{u_email}

mapper.update:
UPDATE USER_INFO
SET U_PWD = #{u_pwd},
U_POSITION = #{u_position},
U_PHONE = #{u_phone}
WHERE U_ID = #{u_id}

mapper.select:
	SELECT U_EMAIL, U_NAME, U_POSITION, U_PHONE, U_ID, DP_NAME
	FROM USER_INFO
	WHERE U_ID = #{u_id}

mapper.update:
	UPDATE USER_INFO
	SET U_EMAIL = #{u_email},
	U_PHONE = #{u_phone},
	U_POSITION = #{u_position}
	WHERE U_ID = #{u_id}
