mapper.select:
SELECT * 
FROM USER_INFO 
WHERE U_EMAIL = #{u_email} AND U_PWD = #{u_pwd}

SELECT COUNT(*)
FROM USER_INFO
WHERE U_EMAIL = #{u_email}

SELECT U_ID, D_ID, U_EMAIL, U_PWD, U_NAME, U_POSITION, U_PHONE, U_STATUS
FROM USER_INFO
WHERE U_ID = #{u_id}

mapper.update:
UPDATE USER_INFO
SET U_PWD = #{u_pwd},
U_PHONE = #{u_phone}
WHERE U_ID = #{u_id}

UPDATE USER_INFO
SET U_EMAIL = #{u_email}, 
U_PHONE = #{u_phone}, 
D_ID = #{d_id},
U_POSITION = #{u_position}
WHERE U_ID = #{u_id}

UPDATE USER_INFO
SET U_STATUS = 'N'
WHERE U_ID = #{u_id}
