mapper.select:
SELECT W_IN, W_OUT, W_TIME, W_STATUS, W_REASON, W_ID
FROM WORK_RECORD
WHERE W_ID = #{w_id}

mapper.update:
UPDATE WORK_RECORD
SET W_OUT = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI')
WHERE W_ID = #{w_id}

UPDATE WORK_RECORD
SET W_STATUS = #{w_status}, W_REASON = #{w_reason}
WHERE W_ID = #{w_id}
